mapper.select:
select reply_num 
from Z_BBSReply  
where USER_ID = #{user_id}

mapper.update:
UPDATE Z_BBSReply
SET reply_likeCnt = ${reply_likeCnt}
WHERE reply_num = ${reply_num}

mapper.insert:
INSERT INTO z_bbsReply (
bbs_num, 
user_id, 
reply_write, 
reply_point, 
reply_likeCnt, 
reply_hateCnt, 
reply_likeCheck, 
reply_date, 
reply_stars
)
VALUES(
 ${bbs_num}
, #{user_id}
, #{reply_write}
, ${reply_point}
, ${reply_likeCnt}
, ${reply_hateCnt}
, 0
, NOW()
, #{reply_stars}
)
